class Solution {
    private int max = 0;
    public int diameterOfBinaryTree(TreeNode root) {
        deep(root);
        return max;
    }
    public int deep(TreeNode root) {
        if (root == null) return 0;
        int left = deep(root.left);
        int right = deep(root.right);
        max = Math.max(max , left + right);
        return Math.max(left , right) + 1;
    }
}

